  `area_id` int UNSIGNED NULL DEFAULT NULL COMMENT '地区编码',
  `address` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '设备详细地址',
